import React, { useState } from "react";
import MoodEntry from "./components/MoodEntry";
import MoodHistory from "./components/MoodHistory";
import Chat from "./components/Chat";
import MoodRecommendations from "./components/MoodRecommendations";
import MoodReminder from "./components/MoodReminder";
import MoodAnalytics from "./components/MoodAnalytics";
import SpotifyPlaylists from "./components/SpotifyPlaylists";

const activeTabButtonStyle = {
  padding: "10px 20px",
  cursor: "pointer",
  border: "1px solid #007bff",
  backgroundColor: "white",
  color: "#007bff",
  fontWeight: "600",
  fontSize: "1rem",
  borderRadius: "4px",
  margin: "0 4px",
  transition: "all 0.2s ease",
};

const tabButtonStyle = {
  padding: "10px 20px",
  cursor: "pointer",
  border: "none",
  backgroundColor: "#007bff",
  color: "white",
  fontWeight: "600",
  fontSize: "1rem",
  borderRadius: "4px",
  margin: "0 4px",
  transition: "all 0.2s ease",
};

export default function App() {
  const [activeTab, setActiveTab] = useState("entry");

  // Central mood state lifted here
  const [selectedMood, setSelectedMood] = useState(null);

  // Fallback mood for cases when none selected yet
  const currentMood = "happy";

  return (
    <div style={{ maxWidth: 800, margin: "auto", fontFamily: "Arial, sans-serif" }}>
      <nav style={{ display: "flex", justifyContent: "center", padding: 20, borderBottom: "1px solid #ccc" }}>
        <button
          onClick={() => setActiveTab("entry")}
          style={activeTab === "entry" ? activeTabButtonStyle : tabButtonStyle}
        >
          Mood Entry
        </button>
        <button
          onClick={() => setActiveTab("history")}
          style={activeTab === "history" ? activeTabButtonStyle : tabButtonStyle}
        >
          Mood History
        </button>
        <button
          onClick={() => setActiveTab("chat")}
          style={activeTab === "chat" ? activeTabButtonStyle : tabButtonStyle}
        >
          Chat
        </button>
        <button
          onClick={() => setActiveTab("recommendations")}
          style={activeTab === "recommendations" ? activeTabButtonStyle : tabButtonStyle}
        >
          Recommendations
        </button>
        <button
          onClick={() => setActiveTab("analytics")}
          style={activeTab === "analytics" ? activeTabButtonStyle : tabButtonStyle}
        >
          Analytics
        </button>
      </nav>

      <main style={{ padding: 20 }}>
        {activeTab === "entry" && (
          <MoodEntry selectedMood={selectedMood} setSelectedMood={setSelectedMood} />
        )}
        {activeTab === "history" && <MoodHistory />}
        {activeTab === "chat" && <Chat />}
        {activeTab === "recommendations" && (
          <>
            <MoodRecommendations />
            <SpotifyPlaylists mood={selectedMood || currentMood} />
          </>
        )}
        {activeTab === "analytics" && <MoodAnalytics />}
      </main>

      <MoodReminder />
    </div>
  );
}
