import React, { useState } from "react";
import { db } from "../firebase";
import { collection, addDoc, serverTimestamp } from "firebase/firestore";

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

const HUGGINGFACE_API_TOKEN = "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";
const SENTIMENT_API_URL = "https://api-inference.huggingface.co/models/distilbert-base-uncased-finetuned-sst-2-english";

export default function MoodEntry() {
  const [mood, setMood] = useState("");
  const [note, setNote] = useState("");
  const [status, setStatus] = useState("");

  // Speech recognition function remains unchanged
  const handleSpeechInput = () => {
    if (!SpeechRecognition) {
      setStatus("Speech Recognition not supported on this browser.");
      return;
    }
    const recognition = new SpeechRecognition();
    recognition.lang = 'en-US';
    recognition.continuous = false;
    recognition.interimResults = false;

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setNote(transcript);
      setStatus("Recorded speech as note!");
    };
    recognition.onerror = (event) => {
      setStatus("Speech Recognition error: " + event.error);
    };
    recognition.start();
  };

  // Function to get sentiment from Hugging Face API
  // Updated function to get sentiment with real error logging 
const getSentiment = async (text) => {
  if (!text || text.trim() === "") {
    return "NO_NOTE";  // Return if note empty to avoid API call
  }
  try {
    const response = await fetch(SENTIMENT_API_URL, {
      method: "POST",
      headers: {
        Authorization: `Bearer ${HUGGINGFACE_API_TOKEN}`,
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ inputs: text })
    });
    
    const result = await response.json();
    if (Array.isArray(result) && result.length > 0) {
      return result[0].label; // 'POSITIVE' or 'NEGATIVE'
    }
    return "UNKNOWN";
  } catch (error) {
    console.error("Sentiment API error:", error);  // Log error details in console
    return "ERROR";
  }
};

const saveMood = async () => {
  if (!mood) {
    setStatus("Please enter a mood");
    return;
  }
  setStatus("Analyzing sentiment...");
  const sentiment = await getSentiment(note);
  try {
    await addDoc(collection(db, "moods"), {
      mood,
      note,
      sentiment,
      createdAt: serverTimestamp()
    });
    setStatus("Mood saved with sentiment: " + sentiment);
    setMood("");
    setNote("");
  } catch (error) {
    setStatus("Error saving mood: " + error.message);
  }
};


  return (
    <div>
      <h2>Enter Your Mood</h2>
      <input
        type="text"
        placeholder="Mood e.g. Happy, Sad..."
        value={mood}
        onChange={(e) => setMood(e.target.value)}
      />
      <br />
      <textarea
        placeholder="Add a note..."
        value={note}
        onChange={(e) => setNote(e.target.value)}
      />
      <br />
      <button onClick={handleSpeechInput}>ðŸŽ¤ Speak Note</button>
      <br />
      <button onClick={saveMood}>Save</button>
      <p>{status}</p>
    </div>
  );
}
