import React, { useState, useEffect } from "react";

export default function SpotifyPlaylists({ mood }) {
  const [playlists, setPlaylists] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Replace with your actual token from get_spotify_token.js
  const ACCESS_TOKEN = "BQD_BpD5GSqAoNU5mrCfmt6Jb_GGP967j9MesA3hIWQBBH70rLQKsjzHDyMhXgRgZ8fRX66I0HLsu2Brk2rf07DZdIhxeUEkHAcVcZlZOM_C4695Zd5VZLDLiF_iIVK9Nmc9Nl8DM94";

  useEffect(() => {
    async function fetchPlaylists() {
      setLoading(true);
      setError(null);

      try {
        const query = encodeURIComponent(mood || "");
        const response = await fetch(
          `https://api.spotify.com/v1/search?q=${query}&type=playlist&limit=5`,
          {
            headers: {
              Authorization: `Bearer ${ACCESS_TOKEN}`,
            },
          }
        );
        if (!response.ok) {
          throw new Error(`Spotify API error: ${response.status}`);
        }
        const data = await response.json();
        setPlaylists(data.playlists.items || []);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    if (mood) {
      fetchPlaylists();
    }
  }, [mood]);

  if (loading) return <p>Loading playlists...</p>;
  if (error) return <p>Error: {error}</p>;
  if (!playlists.length) return <p>No playlists found for "{mood}"</p>;

  return (
    <div>
      <h3>Spotify Playlists for "{mood}" mood</h3>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {playlists
  .filter((playlist) => playlist && playlist.external_urls && playlist.external_urls.spotify)
  .map((playlist) => (
    <li key={playlist.id} style={{ marginBottom: 20 }}>
      <a
        href={playlist.external_urls.spotify}
        target="_blank"
        rel="noopener noreferrer"

              style={{ display: "flex", alignItems: "center", textDecoration: "none", color: "black" }}
            >
              <img
                src={playlist.images[0]?.url}
                alt={playlist.name}
                style={{ width: 64, height: 64, marginRight: 12, borderRadius: 8 }}
              />
              <div>
                <strong>{playlist.name}</strong>
                <br />
                <small>{playlist.tracks.total} tracks</small>
              </div>
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}
