import React, { useState, useEffect } from "react";

export default function SpotifyPlaylists({ mood }) {
  const [playlists, setPlaylists] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [accessToken, setAccessToken] = useState(null);

  // Map your moods to better Spotify search keywords
  const moodMapping = {
    HAPPY: "happy",
    SAD: "sad chill",
    ANGRY: "stress relief calm",
    EXCITED: "party upbeat",
    CALM: "relaxing chill",
  };

  useEffect(() => {
    async function getToken() {
      try {
        const response = await fetch('http://localhost:4000/spotify-token');
        const data = await response.json();
        setAccessToken(data.access_token);
      } catch (error) {
        setError('Failed to fetch Spotify token');
      }
    }
    getToken();
  }, []);

  useEffect(() => {
    if (!accessToken || !mood) return;

    async function fetchPlaylists() {
      setLoading(true);
      setError(null);

      try {
        // Use mapped query keyword or fallback to mood lowercased
        const queryKeyword = moodMapping[mood.toUpperCase()] || mood.toLowerCase();
        const query = encodeURIComponent(queryKeyword);

        const response = await fetch(
          `https://api.spotify.com/v1/search?q=${query}&type=playlist&limit=5`,
          {
            headers: {
              Authorization: `Bearer ${accessToken}`,
            },
          }
        );

        if (!response.ok) {
          throw new Error(`Spotify API error: ${response.status}`);
        }

        const data = await response.json();
        setPlaylists(data.playlists.items.filter(pl => pl && pl.external_urls && pl.external_urls.spotify));
      } catch (err) {
        setError(err.message);
        setPlaylists([]);
      } finally {
        setLoading(false);
      }
    }

    fetchPlaylists();
  }, [accessToken, mood]);

  if (error) return <p>Error: {error}</p>;
  if (loading) return <p>Loading playlists...</p>;
  if (!playlists.length) return <p>No playlists found for "{mood}"</p>;

  return (
    <div>
      <h3>Spotify Playlists for "{mood}" mood</h3>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {playlists.map((playlist) => (
          <li key={playlist.id} style={{ marginBottom: 20 }}>
            <a
              href={playlist.external_urls.spotify}
              target="_blank"
              rel="noopener noreferrer"
              style={{ display: "flex", alignItems: "center", textDecoration: "none", color: "black" }}
            >
              <img
                src={playlist.images[0]?.url}
                alt={playlist.name}
                style={{ width: 64, height: 64, marginRight: 12, borderRadius: 8 }}
              />
              <div>
                <strong>{playlist.name}</strong><br />
                <small>{playlist.tracks.total} tracks</small>
              </div>
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}
