import React, { useEffect, useState } from "react";
import { db } from "../firebase";
import { collection, query, orderBy, limit, getDocs } from "firebase/firestore";

const recommendationsMap = {
  POSITIVE: [
    "Go for a walk outside ðŸ˜Š",
    "Listen to your favorite upbeat song ðŸŽµ",
    "Call a friend and share the good vibes ðŸ“ž"
  ],
  NEGATIVE: [
    "Try some deep breathing exercises ðŸ§˜â€â™‚ï¸",
    "Watch a funny video to lighten your mood ðŸ˜‚",
    "Write down whatâ€™s bothering you ðŸ“"
  ],
  UNKNOWN: [
    "Take a moment to reflect ðŸ¤”",
    "Try a new hobby ðŸŽ¨",
    "Read an inspiring book ðŸ“š"
  ],
  NO_NOTE: [
    "Write down how you're feeling ðŸ“",
    "Talk to a friend or loved one â¤ï¸",
    "Try some meditation ðŸŒ¿"
  ],
  ERROR: [
    "Hmm, something's not right. Try again later âš ï¸"
  ]
};

export default function MoodRecommendations() {
  const [latestSentiment, setLatestSentiment] = useState(null);
  const [recommendations, setRecommendations] = useState([]);

  useEffect(() => {
    const fetchLatestMood = async () => {
      try {
        const q = query(collection(db, "moods"), orderBy("createdAt", "desc"), limit(1));
        const querySnapshot = await getDocs(q);
        if (!querySnapshot.empty) {
          const moodDoc = querySnapshot.docs[0].data();
          setLatestSentiment(moodDoc.sentiment || "UNKNOWN");
        } else {
          setLatestSentiment("NO_NOTE");
        }
      } catch (error) {
        console.error("Error fetching latest mood:", error);
        setLatestSentiment("ERROR");
      }
    };

    fetchLatestMood();
  }, []);

  useEffect(() => {
    if (latestSentiment) {
      setRecommendations(recommendationsMap[latestSentiment] || recommendationsMap["UNKNOWN"]);
    }
  }, [latestSentiment]);

  return (
    <div style={{ maxWidth: 600, margin: "20px auto", padding: 20, borderRadius: 8, border: "1px solid #ddd" }}>
      <h3>Recommended Activities</h3>
      {recommendations.length === 0 ? (
        <p>Loading recommendations...</p>
      ) : (
        <ul>
          {recommendations.map((rec, idx) => (
            <li key={idx} style={{ marginBottom: 10 }}>{rec}</li>
          ))}
        </ul>
      )}
    </div>
  );
}
