import React, { useState } from "react";
import { db } from "../firebase";
import { collection, addDoc, serverTimestamp } from "firebase/firestore";

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;

const HUGGINGFACE_API_TOKEN = "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";
const SENTIMENT_API_URL = "https://api-inference.huggingface.co/models/distilbert/distilbert-base-uncased-finetuned-sst-2-english";

// Emoji and color mapping for UI feedback
const sentimentEmoji = {
  POSITIVE: "üòä",
  NEGATIVE: "üòî",
  UNKNOWN: "ü§î",
  NO_NOTE: "üìù",
  ERROR: "‚ö†Ô∏è"
};

const sentimentColor = {
  POSITIVE: "green",
  NEGATIVE: "red",
  UNKNOWN: "gray",
  NO_NOTE: "blue",
  ERROR: "orange"
};

export default function MoodEntry() {
  const [mood, setMood] = useState("");
  const [note, setNote] = useState("");
  const [status, setStatus] = useState("");
  const [sentiment, setSentiment] = useState(""); // Track detected sentiment

  const handleSpeechInput = () => {
    if (!SpeechRecognition) {
      setStatus("Speech Recognition not supported on this browser.");
      setSentiment("ERROR");
      return;
    }
    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.continuous = false;
    recognition.interimResults = false;

    recognition.onresult = (event) => {
      const transcript = event.results[0][0].transcript;
      setNote(transcript);
      setStatus("Recorded speech as note!");
      setSentiment(""); // Clear previous sentiment feedback
    };
    recognition.onerror = (event) => {
      setStatus("Speech Recognition error: " + event.error);
      setSentiment("ERROR");
    };
    recognition.start();
  };

  const getSentiment = async (text) => {
    if (!text || text.trim() === "") {
      return "NO_NOTE";
    }
    try {
      const response = await fetch(SENTIMENT_API_URL, {
        method: "POST",
        headers: {
          Authorization: `Bearer ${HUGGINGFACE_API_TOKEN}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ inputs: text.trim() }),
      });

      const result = await response.json();
      console.log("Sentiment API response:", JSON.stringify(result, null, 2));

      if (Array.isArray(result)) {
        let data = result;

        if (Array.isArray(result[0])) {
          data = result[0];
        }

        if (data.length === 2) {
          return data[0].score > data[1].score ? data[0].label : data[1].label;
        } else if (data.length === 1) {
          return data[0].label || "UNKNOWN";
        }
      }
      return "UNKNOWN";
    } catch (error) {
      console.error("Sentiment API error:", error);
      return "ERROR";
    }
  };

  const saveMood = async () => {
    if (!mood) {
      setStatus("Please enter a mood");
      setSentiment("ERROR");
      return;
    }
    setStatus("Analyzing sentiment...");
    setSentiment("");

    const detectedSentiment = await getSentiment(note);
    setSentiment(detectedSentiment);

    if (!detectedSentiment || detectedSentiment === "ERROR") {
      setStatus("Sentiment analysis failed, not saving mood.");
      return;
    }

    try {
      const dataToSave = {
        mood,
        note,
        sentiment: detectedSentiment,
        createdAt: serverTimestamp(),
      };
      await addDoc(collection(db, "moods"), dataToSave);

      setStatus("Mood saved with sentiment: " + detectedSentiment);
      setMood("");
      setNote("");
    } catch (error) {
      setStatus("Error saving mood: " + error.message);
      setSentiment("ERROR");
    }
  };

  return (
    <div>
      <h2>Enter Your Mood</h2>
      <input
        type="text"
        placeholder="Mood e.g. Happy, Sad..."
        value={mood}
        onChange={(e) => setMood(e.target.value)}
      />
      <br />
      <textarea
        placeholder="Add a note..."
        value={note}
        onChange={(e) => setNote(e.target.value)}
      />
      <br />
      <button onClick={handleSpeechInput}>üé§ Speak Note</button>
      <br />
      <button onClick={saveMood}>Save</button>
      <p style={{ color: sentimentColor[sentiment] || "black", fontWeight: "bold", fontSize: "1.1em" }}>
        {sentimentEmoji[sentiment] || "‚ùì"} {status}
      </p>
    </div>
  );
}
